async def fetch_tv_async(workout_length, page, without_genres, genres, watch_providers, watch_region):
    async with aiohttp.ClientSession() as session:
        return await fetch_tv(workout_length, page, without_genres, genres, watch_providers, watch_region, session)


async def fetch_tv(workout_length, page, without_genres, genres, watch_providers, watch_region, session):
    try:
        discover_tv_response = await session.get(get_discover_tv_query_url(workout_length=workout_length, page=page, without_genres=without_genres, genres=genres, watch_providers=watch_providers, watch_region=watch_region))
        discovered_tv_shows = await discover_tv_response.json()

        tv_shows = await asyncio.gather(*[fetch_single_tv_show(tv_show, session) for tv_show in discovered_tv_shows['results']])

        final_tv_shows = {'page': int(
            page), 'tv_shows': tv_shows, 'total_pages': discovered_tv_shows['total_pages']}
        return final_tv_shows
    except Exception as e:
        print(e)


def get_discover_tv_query_url(workout_length, page, without_genres, genres, watch_providers, watch_region):
    if without_genres is None:
        return f"{TMDB_API_DISCOVER_TV_URL}&with_genres={genres}&with_watch_providers={watch_providers}&with_runtime.gte={workout_length}&with_runtime.lte={int(workout_length) + 5}&with_watch_monetization_types=flatrate&watch_region={watch_region}&page={page}"
    else:
        return f"{TMDB_API_DISCOVER_TV_URL}&with_genres={genres}&without_genres={without_genres}&with_watch_providers={watch_providers}&with_runtime.gte={workout_length}&with_runtime.lte={int(workout_length) + 5}&with_watch_monetization_types=flatrate&watch_region={watch_region}&page={page}"


async def fetch_single_tv_show(tv_show, session):
    tv_show_details_response = await session.get(TMDB_API_TV_DETAIL_URL.format(tv_show['id']) + '&append_to_response=watch/providers,details')
    current_tv_show = await tv_show_details_response.json()
    image_path = None
    if current_tv_show['poster_path'] is not None:
        image_path = current_tv_show['poster_path']
    else:
        image_path = current_tv_show['backdrop_path']    
    return {
        'id': current_tv_show['id'],
        'name': current_tv_show['name'],
        'episode_runtime': int(statistics.mean(current_tv_show['episode_run_time'])),
        'number_of_episodes': current_tv_show['number_of_episodes'],
        'number_of_seasons': current_tv_show['number_of_seasons'],
        'genres': current_tv_show['genres'],
        'image_url': TMDB_IMAGES_BASE_URL + f"{image_path}",
        'vote_average': current_tv_show['vote_average'],
        'overview': current_tv_show['overview'],
        'production_countries': current_tv_show['production_countries'],
        'watch_providers': current_tv_show['watch/providers']['results'], 'tmdb_image_base_url': TMDB_IMAGES_BASE_URL
    }


async def fetch_movies(page, without_genres, genres, watch_providers, watch_region, session):
    try:
        discover_movies_response = await session.get(get_discover_query_url(page=page, without_genres=without_genres, genres=genres, watch_providers=watch_providers, watch_region=watch_region))
        discovered_movies = await discover_movies_response.json()

        flask.globals.g.avg_runtime = 0
        movies = await asyncio.gather(*[fetch_single_movie(movie, session) for movie in discovered_movies['results']])
        flask.globals.g.avg_runtime = flask.globals.g.avg_runtime // len(
            movies)
        final_movies = {'page': int(
            page), 'movies': movies, 'total_pages': discovered_movies['total_pages'], 'avg_runtime': flask.globals.g.avg_runtime}
        return final_movies
    except Exception as e:
        print(e)
        return


async def fetch_single_movie(movie, session):
    movie_details_response = await session.get(TMDB_API_MOVIE_DETAIL_URL.format(movie['id']) + '&append_to_response=watch/providers,details')
    current_movie = await movie_details_response.json()
    flask.globals.g.avg_runtime += current_movie['runtime']
    image_path = None
    if current_movie['poster_path'] is not None:
        image_path = current_movie['poster_path']
    else:
        image_path = current_movie['backdrop_path']  
    return {
        'id': current_movie['id'],
        'title': current_movie['title'],
        'genres': current_movie['genres'],
        'image_url': TMDB_IMAGES_BASE_URL + f"{image_path}",
        'vote_average': current_movie['vote_average'],
        'runtime': current_movie['runtime'],
        'overview': current_movie['overview'],
        'homepage': current_movie['homepage'],
        'production_countries': current_movie['production_countries'],
        'watch_providers': current_movie['watch/providers']['results'], 'tmdb_image_base_url': TMDB_IMAGES_BASE_URL
    }


def get_discover_query_url(page, without_genres, genres, watch_providers, watch_region):
    if without_genres is None:
        return f"{TMDB_API_DISCOVER_MOVIES_URL}&with_genres={genres}&with_watch_providers={watch_providers}&with_runtime.lte=210&with_runtime.gte=120&with_watch_monetization_types=flatrate&watch_region={watch_region}&page={page}&sort_by=vote_average.desc"
    else:
        return f"{TMDB_API_DISCOVER_MOVIES_URL}&with_genres={genres}&without_genres={without_genres}&with_watch_providers={watch_providers}&with_runtime.lte=210&with_runtime.gte=120&with_watch_monetization_types=flatrate&watch_region={watch_region}&page={page}&sort_by=vote_average.desc"


async def fetch_movies_async(page, without_genres, genres, watch_providers, watch_region):
    async with aiohttp.ClientSession() as session:
        return await fetch_movies(page=page, without_genres=without_genres, genres=genres, watch_providers=watch_providers, watch_region=watch_region, session=session)


